import React, { useState, useEffect } from "react";
import axios from "axios";

const Transactions = ({ selectedMonth }) => {
    const [transactions, setTransactions] = useState([]);
    const [search, setSearch] = useState("");
    const [page, setPage] = useState(1);
    const [total, setTotal] = useState(0);

    useEffect(() => {
        const fetchTransactions = async () => {
            const { data } = await axios.get("http://localhost:5000/api/transactions", {
                params: { page, perPage: 10, search, month: selectedMonth },
            });
            setTransactions(data.transactions);
            setTotal(data.total);
        };
        fetchTransactions();
    }, [page, search, selectedMonth]);

    return (
        <div>
            <input
                type="text"
                placeholder="Search"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
            />
            <table>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Date of Sale</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((tx) => (
                        <tr key={tx._id}>
                            <td>{tx.title}</td>
                            <td>{tx.description}</td>
                            <td>{tx.price}</td>
                            <td>{tx.dateOfSale}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => setPage((prev) => Math.max(prev - 1, 1))}>
                Previous
            </button>
            <button
                onClick={() => setPage((prev) => (page * 10 < total ? prev + 1 : prev))}
            >
                Next
            </button>
        </div>
    );
};

export default Transactions;
