import React from "react";
import { Chart, ArcElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';  // Example using Line chart, can be any type of chart

// Register necessary components (ArcElement, LinearScale, Tooltip, Legend, CategoryScale)
Chart.register(ArcElement, CategoryScale, LinearScale, Tooltip, Legend);

const Dropdown = ({ selectedMonth, setSelectedMonth }) => {
    const months = [
        { value: "01", label: "January" },
        { value: "02", label: "February" },
        { value: "03", label: "March" },
        { value: "04", label: "April" },
        { value: "05", label: "May" },
        { value: "06", label: "June" },
        { value: "07", label: "July" },
        { value: "08", label: "August" },
        { value: "09", label: "September" },
        { value: "10", label: "October" },
        { value: "11", label: "November" },
        { value: "12", label: "December" },
    ];

    return (
        <select
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(e.target.value)}
        >
            {months.map((month) => (
                <option key={month.value} value={month.value}>
                    {month.label}
                </option>
            ))}
        </select>
    );
};

export default Dropdown;
